import React, {Component} from 'react';
import IngredientForm from '../../IngredientForm';

class AddIngredients extends Component {

    constructor(props) {
        super(props);
        this.state = { 
            showForm: false,
            items: []
        }

        this.addItem = this.addItem.bind(this);
    }

    _showForm = (bool) => {
        this.setState({
            showForm: bool
        });
    }

    render() {
        return (
            <div> 
                <button onClick={this._showForm.bind(null, true)}>Add Ingredients</button>
                <button onClick={this._showForm.bind(null, false)}>Hide Form</button>
                { this.state.showForm ? <IngredientForm meal={this.props.meal} /> : '' }
            </div>
        );
    }
}

export default AddIngredients
