import React, {Component} from 'react';
import Meal from '../Meal/Meal';
import AddIngredients from '../AddIngredient/AddIngredients'

class MealList extends Component {
    constructor() {
        super();
        this.state = { showAddIngredients: false }
    }

    _showAddIngredients = (bool) => {
        this.setState({
            showAddIngredients: bool
        });
    }

    render() {
        return (
            <div>
                <ul>
                    {this.props.meals.map((meal) => 
                        <li key={meal.name}>
                            <Meal name={meal.name} ingredients={meal.ingredients}/>
                            <button onClick={this._showAddIngredients.bind(null, true)}>show</button>
                            { this.state.showAddIngredients && (<AddIngredients />) }
                        </li>
                    )}

                    {/* <li><Meal name={} ingredients={["Pepperoni", "Dough"]} /></li>
                    <li><Meal name={} ingredients={["Cheese", "Bread"]} /></li> */}
                </ul>
            </div>
        );
    }
}

export default MealList;
