import React from 'react';

export default class IngredientForm extends React.Component {

    constructor(props) {
        super(props);
        this.ingredientInput = React.createRef();
        this.amountInput = React.createRef();
        this.measurementInput = React.createRef();
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(e) {
        e.preventDefault();
        if (this.inputRef.current.value !== "") {
            var newItem = {
                text: this.inputRef.current.value,
                key: Date.now()
            };

            this.props.addItem(newItem);

            this.inputRef.current.value = "";
        }

    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <input ref={this.ingredientInput} placeholder="Enter Ingredient" className="ingredient"></input>
                <input ref={this.amountInput} placeholder="Enter Amount" className="amount"></input>
                <input ref={this.measurementInput} placeholder="Enter Measurement" className="measurement"></input>
                <button type="submit">+</button>
            </form>
        );
    }
}
