import React from 'react';

export default class IngredientForm extends React.Component {

    constructor(props) {
        super(props);
        this.inputRef = React.createRef();
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.state = {
            items: []
        };
    }
    handleChange(event) {
        this.setState({mealName: event.target.value});
    }
    handleSubmit(e) {
        e.preventDefault();
        this.props.meal.ingredients = this.state.items
        // if (this.ingredientInput.current.value !== "") {
        //     var newItem = {
        //         text: this.ingredientInput.current.value,
        //         key: Date.now()
        //     };

        //     this.props.addItem(newItem);

        //     this.inputRef.current.value = "";
        // }

    }

    render() {
        return (
            <div>  
                <ul>
                    {this.state.items.map((item) => 
                        <li>{item}</li>
                    )}
                </ul>
                <form onSubmit={this.handleSubmit}>
                    <input value={this.state.mealName} onChange={this.handleChange} placeholder="Enter Ingredient" className="ingredient"></input>
                    <button type="submit">+</button>
                </form>
            </div>
        );
    }
}
