import React, {Component} from 'react';

class ReportArea extends Component {

    constructor(props) {
        super(props);
        this.state = { 
        }
    }

    handleNewIngredientNameChange(e) {
        this.setState({newIngredientName: e.target.value});
    }

    render() {
        return (
            <div>
                <h1>Report Area</h1>
                <h2>Check what you want to eat this week</h2>
                <form> 
                    {this.props.meals.map((meal, index) => (
                        <span key={index}>
                            <label>{meal.name}</label>
                            <input
                                type="checkbox"
                                className="checkbox-no-break"
                                onChange={this.handleReportCheckboxChange} 
                                name={index}
                            />

                            <label htmlFor={index}>{meal.name}</label>
                        </span>
                    ))}
                </form>
            </div>
        );
    }
}

export default ReportArea
