import React from 'react';

export default class IngredientForm extends React.Component {
    addItem(e) {
        e.preventDefault();
        if (this._inputElement.value !== "") {
            var newItem = {
                text: this._inputElement.value,
                key: Date.now()
            };

            this.setState((prevState) => {
                return { 
                    items: prevState.items.concat(newItem) 
                };
            });

            this._inputElement.value = "";
        }

        console.log(this.state.items);

    }

    render() {
        return (
            <form onClick={this.props.addItem}>
                <input ref={(a) => this._inputElement = a} placeholder="Enter Ingredient" className="ingredient"></input>
                <input ref={(a) => this._inputElement = a} placeholder="Enter Amount" className="amount"></input>
                <input ref={(a) => this._inputElement = a} placeholder="Enter Measurement" className="measurement"></input>
                <button type="submit">+</button>
            </form>
        );
    }
}
