import Meal from '../Meal/Meal';
import AddIngredients from '../AddIngredient/AddIngredients'
import styles from './MealList.module.css';

class MealList extends Component {
    constructor(props) {
        super(props);

        this.state = {
            showIngredientsForm: false
        }
    }

    render() {
        console.log(styles)
        return (
            // <div className={styles.mealListContainer}>
            <div>
                {/* <ul className={styles.mealList}> */}
                    {this.props.meals.map((meal, index) => 
                        <div key={index} className="card card-primary">
                            <div className="card-header">{meal.name}</div>
                            { this.state.showIngredientsForm ? 
                                <div className="card-body"><IngredientForm meal={meal} /></div>
                                : '' }
                        </div>
                        // <li key={index}>
                        //     <Meal name={meal.name} ingredients={meal.ingredients}/>
                        //     <AddIngredients meal={meal} />
                        // </li>
                    )}
                {/* </ul> */}
            </div>
        );
    }
}

export default MealList;
