import React, {Component} from 'react';
import IngredientForm from '../IngredientForm/IngredientForm';
import './Meal.module.css';

class Meal extends Component {

    constructor(props) {
        super(props);
        this.state = { 
            showForm: false,
            items: []
        }
    }

    _showForm = (bool) => {
        this.setState({
            showForm: bool
        });
    }

    handleReportCheckboxChange(meal) {
        this.props.meal.isEnabledForReport = event.target.checked
    }

    render() {
        return (
            <div className="card card-primary">
                <div className="card-header">
                    {this.props.meal.name}
                    <div className="pull-right">
                        <input 
                            type="checkbox" 
                            name="enableForReport" 
                            onChange={this.handleReportCheckboxChange(this.props.meal)} 
                            checked={this.props.meal.isEnabledForReport} />
                        <label htmlFor="enableForReport">Enable in report</label>
                        <button className="btn btn-sm btn-light" onClick={this._showForm.bind(null, true)}>Open</button>
                        <button className="btn btn-sm btn-dark" onClick={this._showForm.bind(null, false)}>Close</button>
                    </div>
                </div>
                { this.state.showForm ? <div className="card-body"> 
                    <IngredientForm meal={this.props.meal} />
                </div> : '' }
            </div>
        );
    }
}

export default Meal
