import React, {Component} from 'react';
import IngredientForm from '../IngredientForm/IngredientForm';
import './Meal.module.css';

class Meal extends Component {

    constructor(props) {
        super(props);
        this.state = { 
            showForm: false,
            items: []
        }
    }

    _showForm = (bool) => {
        this.setState({
            showForm: bool
        });
    }

    render() {
        return (
            <div className="card card-primary">
                <div className="card-header">{this.props.meal.name}</div>
                <div className="card-body"> 
                    <button className="btn btn-sm btn-light" onClick={this._showForm.bind(null, true)}>Show Ingredients</button>
                    <button className="btn btn-sm btn-dark" onClick={this._showForm.bind(null, false)}>Hide Ingredients</button>
                    { this.state.showForm ? <IngredientForm meal={this.props.meal} /> : '' }
                </div>
            </div>
        );
    }
}

export default AddIngredients
