import React from 'react';

export default class IngredientForm extends React.Component {

    constructor(props) {
        super(props);
        this.inputRef = React.createRef();
        this.handleSave = this.handleSave.bind(this);
        this.handleAddIngredient = this.handleAddIngredient.bind(this);
        this.state = {
            items: []
        };
    }

    handleAddIngredient(e) {
        e.preventDefault();
        this.setState((prevState) => {
           return { 
               items: prevState.items.concat(e.target.value) 
           };
        });
    }

    handleSave(e) {
        e.preventDefault();
        this.props.meal.ingredients = this.state.items
        // if (this.ingredientInput.current.value !== "") {
        //     var newItem = {
        //         text: this.ingredientInput.current.value,
        //         key: Date.now()
        //     };

        //     this.props.addItem(newItem);

        //     this.inputRef.current.value = "";
        // }

    }

    render() {
        return (
            <div>  
                <ul>
                    {this.state.items.map((item) => 
                        <li>{item}</li>
                    )}
                </ul>
                <form onSubmit={this.handleAddIngredient}>
                    <input 
                        value={this.state.newIngredientName} 
                        placeholder="Add Ingredient" 
                        className="ingredient" />

                    <button type="submit">+</button>
                </form>
                <button onSubmit={this.handleSave} type="submit">Save</button>
            </div>
        );
    }
}
