import React, { Component } from 'react';
import MealList from '../MealList/MealList';
import AddMeal from '../AddMeal/AddMeal'

class FridgeFriendApp extends Component {
    constructor(props) {
        super(props)
        this.state = {meal: []};
    }

    addItem(meal) {
        var addMeal = []
        addMeal.push(meal)
        this.state = {meal: []};

    }

    render() {
        return (
            <div>
            <h1>Fridge Friend</h1>
            <MealList />
            <AddMeal />
        </div>
        )
    }
}

export default FridgeFriendApp;
