import React, {Component} from 'react';
import IngredientForm from '../IngredientForm/IngredientForm';
import styles from './Meal.module.css';

class Meal extends Component {

    constructor(props) {
        super(props);
        this.state = { 
            showForm: false,
            items: []
        }
    }

    _showForm = (bool) => {
        this.setState({
            showForm: bool
        });
    }

    delete() {
        //this.props.meal.splice()
    }

    render() {
        return (
            <div className="card card-primary">
                <div className="card-header">
                    <div className="row-sm-6">{this.props.meal.name}</div> 
                    <div className="row-sm-6">
                        <button className="btn btn-sm btn-light" onClick={this._showForm.bind(null, true)}>Open</button>
                        <button className="btn btn-sm btn-dark" onClick={this._showForm.bind(null, false)}>Close</button>
                        <button className="btn btn-sm btn-danger" onClick={() => this.delete()}>Delete</button>
                        <button className="btn btn-sm btn-warning" onClick={this._showForm.bind(null, false)}>Edit</button>
                    </div>
                </div>
                { this.state.showForm ? <div className="card-body"> 
                    <IngredientForm meal={this.props.meal} />
                </div> : '' }
            </div>
        );
    }
}

export default Meal
